import React, { Component } from 'react';
import './MainContent.css';

class MainContent extends Component {
  render() {

    function ani(){
      document.getElementById('sierra').className='classname';
    }

    return (
      <div className="main-content">
        <p className="main-content__title " id="sierra">Sierra Nevada</p>
        <p className="main-content__subtitle">Mountain Range, Spain</p>
        <button className="main-content__button" onclick={ani()}>View Details</button>
      </div>
    );
  }
}

export default MainContent;
